import React from 'react';
import { connect } from 'react-redux';
import { compose } from 'redux';
import ItemCard from './ItemCard';
import { firestoreConnect } from 'react-redux-firebase';
import { Rnd } from 'react-rnd';

export default class DraggableItem extends React.Component {
    callDisplay = (e) => {
        this.props.display(this.props.currentItem.key);
    }

    render() {
        if(this.props.currentItem != null){
        var borderString = "solid " + this.props.currentItem.border_thickness + "px " + this.props.currentItem.border_color;
        var font = this.props.currentItem.font_size + "px";
        var borderRadius = this.props.currentItem.border_radius + "px";
        console.log(borderString);
        if(this.props.currentItem.description === "container"){
            return (
                <Rnd 
                            style = {{
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: font,
                                borderRadius: borderRadius,
                                border: borderString,
                                background: this.props.currentItem.background,
                            }}
                            default={{
                                    x: this.props.currentItem.position_x,
                                    y: this.props.currentItem.position_y,
                                    width: this.props.currentItem.width,
                                    height: this.props.currentItem.height,

                            }}
                            onClick={this.callDisplay}
                            ></Rnd>
            );
        } else if(this.props.currentItem.description === "textbox"){
            return (
                <Rnd 
                            style = {{
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: font,
                                borderRadius: borderRadius,
                                border: borderString,
                                background: this.props.currentItem.background,
                            }}
                            default={{
                                    x: this.props.currentItem.position_x,
                                    y: this.props.currentItem.position_y,
                                    width: this.props.currentItem.width,
                                    height: this.props.currentItem.height,
                            }}
                            onClick={this.callDisplay}
                            >{this.props.currentItem.properties}</Rnd>
            );
        } else if(this.props.currentItem.description === "button"){
            return (
                <Rnd 
                            style = {{
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: font,
                                borderRadius: borderRadius,
                                border: borderString,
                                background: this.props.currentItem.background,
                            }}
                            default={{
                                    x: this.props.currentItem.position_x,
                                    y: this.props.currentItem.position_y,
                                    width: this.props.currentItem.width,
                                    height: this.props.currentItem.height,
                            }}
                            onClick={this.callDisplay}
                            >{this.props.currentItem.properties}</Rnd>
            );
        } else if(this.props.currentItem.description === "label"){
            return (
                <Rnd 
                            style = {{
                                display: "flex",
                                alignItems: "center",
                                justifyContent: "center",
                                fontSize: font,
                                borderRadius: borderRadius,
                                border: borderString,
                                background: this.props.currentItem.background,
                            }}
                            default={{
                                    x: this.props.currentItem.position_x,
                                    y: this.props.currentItem.position_y,
                                    width: this.props.currentItem.width,
                                    height: this.props.currentItem.height,
                            }}
                            onClick={this.callDisplay}
                            >{this.props.currentItem.properties}</Rnd>
            );
        } else {
            return (console.log("WHERE IS MY STUFF"));
        }
    }
    }
}

